mapper.delete:
DELETE FROM cart
WHERE member_num = #{member_num}
	AND cart_group_id = #{cart_group_id}

mapper.update:
UPDATE cart SET 
	cart_quantity = #{cart_quantity},
	item_size = #{item_size, jdbcType=VARCHAR}

mapper.select:
SELECT * FROM cart
WHERE member_num = #{member_num}
	AND item_id = #{item_id}
